class Solution {
public:
    int dp[50001], cur[50001];
    bool rep[50001];
    int minimumCost(string target, vector<string>& w, vector<int>& c) {
        unordered_map <string, int> m;
        vector <string> words;
        vector <int> costs;
        for (int i = 0; i<w.size(); ++i){
            if (m[w[i]] == 0){
                m[w[i]] = c[i];
                words.push_back(w[i]);
            }else{
                m[w[i]] = min(m[w[i]], c[i]);
            }
        }

        for (auto x : words){
            costs.push_back(m[x]);
        }


        for (int i = 1; i<=target.size(); ++i){
            dp[i] = -1;
        }

        int cnt = 0;
        for (string x : words){
            bool f = 0;
            for (int i = 0; i<x.size() - 1; ++i){
                if (x[i] != x[i + 1]){
                    f = 1;
                    break;
                }
            }
            if (f == 0){
                rep[cnt] = 1;
            }
            ++cnt;
        }


        for (int i = 1; i<=target.size(); ++i){
            for (int j = 0; j<words.size(); ++j){
                if (words[j][cur[j]] == target[i - 1]){
                    ++cur[j];
                }else{
                    cur[j] = 0;
                    if (words[j][cur[j]] == target[i - 1]){
                        ++cur[j];
                    }else{
                        cur[j] = 0;
                    }
                }



                if (cur[j] == words[j].size()){
                    if (dp[i - words[j].size()] != -1){
                        if (dp[i] == -1) { dp[i] = dp[i - words[j].size()] + costs[j]; }
                        else { dp[i] = min(dp[i], dp[i - words[j].size()] + costs[j]); }
                    }

                    if (rep[j] == 1){
                        --cur[j];
                    }else{
                        cur[j] = 1;
                    }
                }
            }
        }

        return dp[target.size()];
    }
};
