struct t_t { t_t*n[26]{}; int e{}; };
class Solution {
public:
    int minimumCost(string G, vector<string>& W, vector<int>& C) {
        t_t T;G.insert(0,"~");
        for(int i=0;i<W.size();++i){
            auto t=&T;for(auto c:W[i]){auto&n=t->n[c-'a'];if(!n)n=new t_t;t=n;}
            if(!t->e||t->e>C[i])t->e=C[i];
        }
        vector<uint>D(G.size(),-1);D[0]=0;
        for(int b=0;b<G.size();++b)if(D[b]!=-1){
            auto t=&T;for(int i=b;++i<G.size();){
                t=t->n[G[i]-'a'];if(!t)break;if(t->e){
                    D[i]=min(D[i],D[b]+t->e);
                }
            }
        }
        return D.back();
    }
};
