class Solution {
public:
    #define ll long long
    const ll mx = 1e10;
    ll dp[50010];
    struct Trie{
        int cost;
        int next[26];
        Trie(){
            cost = -1;
            for(int i=0;i<26;i++)next[i] = -1;
        }
    };
    vector<Trie> T;
    void built(string &s,int cs){
        int v=0;
        for(auto c: s){
            int ind = c - 'a';
            if(T[v].next[ind] == -1){
                T[v].next[ind] = T.size();
                T.emplace_back();
            }
            v = T[v].next[ind];
        // cout<<s<<" "<<cs<<" "<<v<<endl;
        }

        if(T[v].cost == -1)T[v].cost = cs;
        else T[v].cost = min(T[v].cost,cs);
    }

    ll solve_dp(int ind,string& tar){
        if(ind >= tar.size())return 0;
        if(dp[ind] != -1)return dp[ind];
        ll &ans = dp[ind];
        ans = mx;
        int v =0 ;
        for(int i= ind;i<tar.size();i++){
            int ind1 = tar[i] - 'a';
            if(T[v].next[ind1] == -1)return ans;
            v = T[v].next[ind1];
            // cout<<i<<" "<<v<<" "<<T[v].cost<<endl;
            if(T[v].cost != -1)ans = min(ans,1LL*T[v].cost+solve_dp(i+1,tar));
        }
        // cout<<ind<<" "<<ans<<endl;
        return ans;
    }


    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        T.emplace_back();
        int n = costs.size();
        for(int i=0;i<n;i++){
            built(words[i],costs[i]);
        }
        memset(dp,-1,sizeof(dp));
        ll ans = solve_dp(0,target);
        if(ans >= mx)ans = -1;
        return ans;
    }
};
