class Trie{
    public:
        Trie* a[26];
        bool flag;
        int cost;
        Trie(){
            for(int i=0;i<26;i++){
                a[i] = NULL;
            }
            flag = false;
            cost = 0;
        }
        void insert(string s, int cost){
            Trie* curr = this;
            for(int i=0;i<s.size();i++){
                if(curr->a[s[i]-'a'] == NULL){
                    curr->a[s[i]-'a'] = new Trie();
                }
                curr = curr->a[s[i]-'a'];
            }
            // curr->flag = true;
            // curr->cost = cost;
            if(curr->flag == false){
                curr->flag = true;
                curr->cost = cost;
            }
            else{
                curr->cost = min(curr->cost, cost);
            }
        }
};


class Solution {
public:

    int solve(string&target, Trie&temp, int i,vector<int>&dp){
        // Base condition
        if(i >= target.size()) return 0;
        //
        if(dp[i] != -1) return dp[i];
        Trie* curr = &temp;
        int ans = 1e9;
        for(int j=i;j<target.size();j++){
            if(curr->a[target[j]-'a'] == NULL){
                break;
            }
            curr = curr->a[target[j]-'a'];
            if(curr->flag){
                ans = min(ans, curr->cost + solve(target, temp, j+1, dp));
            }
        } 
        return dp[i] = ans;
        //

    }

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        map<string, int> map1;
        for(int i=0;i<words.size();i++){
            if(map1.find(words[i]) != map1.end()){
                map1[words[i]] = min(map1[words[i]], costs[i]);
            }
            else{
                map1[words[i]] = costs[i];
            }
        }
        Trie temp;
        for(auto it : map1){
            temp.insert(it.first, it.second);
        }
        vector<int> dp(target.size()+1, -1);
        int ans = solve(target, temp, 0, dp);
        if(ans >= 1e9) return -1;
        return ans;
    }
};
