class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        # Create a dictionary to map words to their costs
        word_cost_map = {}
        for word, cost in zip(words, costs):
            if word not in word_cost_map or word_cost_map[word] > cost:
                word_cost_map[word] = cost

        # Iterate over the target string
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            for word, cost in word_cost_map.items():
                if target.startswith(word, i):
                    dp[i + len(word)] = min(dp[i + len(word)], dp[i] + cost)

        return dp[n] if dp[n] != float('inf') else -1
