class Solution {
    int dp[];
    public int minimumCost(String target, String[] words, int[] costs) {
        dp=new int[target.length()];
        Arrays.fill(dp,-2);
        Trie head=new Trie();
        for(int i=0;i<words.length;i++){
            insert( head,  words[i],  costs[i]);
        }
        int res=minCost( target, head,  0);
        if(res==Integer.MAX_VALUE)
            return -1;
            return res;

    }
    public int minCost(String target,Trie head, int index){
        int min=Integer.MAX_VALUE;
        if(index>=target.length())
            return 0;
        if(dp[index]!=-2)
            return dp[index];
            Trie head1=head;
            int in=index;
        while(index<target.length()&&head.child[target.charAt(index)-97]!=null){
            head=head.child[target.charAt(index)-97];
            index++;
            if(head.isEnd){
                //System.out.println(index+" "+head.cost);
                int next=minCost( target, head1,  index);
                if(next<Integer.MAX_VALUE){
                    next+=head.cost;
                    min=Math.min(min,next);
                }
            }
        }
        dp[in]=min;
        return min;
    }
    public void insert(Trie head, String word, int cost){
        for(int i=0;i<word.length();i++){
            char c=word.charAt(i);
            if(head.child[c-97]==null)
                head.child[c-97]=new Trie();
            head=head.child[c-97];
        }
        head.isEnd=true;
        if(head.cost==0||head.cost>cost)
            head.cost=cost;

    }
}
class Trie{
    Trie[] child=new Trie[26];
    boolean isEnd;
    int cost;
}
