class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordIndices; // (index of word in the words array, length of word)
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (node->children.find(c) == node->children.end()) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back({index, (int)word.length()});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (node->children.find(target[j]) == node->children.end()) break;
                node = node->children[target[j]];
                for (auto& [index, length] : node->wordIndices) {
                    if (j - i + 1 == length) {
                        dp[j + 1] = min(dp[j + 1], dp[i] + costs[index]);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
