class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> children;
        vector<pair<int, int>> wordEndings; 
    };

    class Trie {
    public:
        TrieNode* root;
        Trie() { root = new TrieNode(); }

        void insert(string& word, int idx, int cost) {
            TrieNode* node = root;
            for (char ch : word) {
                if (!node->children.count(ch))
                    node->children[ch] = new TrieNode();
                node = node->children[ch];
            }
            node->wordEndings.push_back({idx, cost});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int len = target.size();
        vector<int> dp(len + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < len; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.root;
            for (int j = i; j < len; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (auto& pair : node->wordEndings) {
                    int wordIdx = pair.first;
                    int wordCost = pair.second;
                    int wordLen = words[wordIdx].size();
                    if (i + wordLen <= len) {
                        dp[i + wordLen] = min(dp[i + wordLen], dp[i] + wordCost);
                    }
                }
            }
        }

        return dp[len] == INT_MAX ? -1 : dp[len];
    }
};
