class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    int cost;

    TrieNode() : cost(INT_MAX) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->cost = min(node->cost, cost);  // Store the minimum cost at the end node
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();

        Trie trie;
        for (size_t i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;  


        for (int j = 0; j < n; ++j) {
            if (dp[j] == INT_MAX) continue;  

            TrieNode* node = trie.root;

            for (int k = j; k < n; ++k) {
                if (!node->children.count(target[k])) break;
                node = node->children[target[k]];
                if (node->cost != INT_MAX) {
                    dp[k + 1] = min(dp[k + 1], dp[j] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
