class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> wordIndices; 
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->wordIndices.push_back({index, (int)word.length()});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (auto& p : node->wordIndices) {
                    int wordIndex = p.first;
                    int len = p.second;
                    if (j + 1 >= i + len) {
                        dp[j + 1] = min(dp[j + 1], dp[i] + costs[wordIndex]);
                    }
                }
            }
        }
        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
