class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<int> wordIndices;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char ch : word) {
            if (node->children.find(ch) == node->children.end()) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->wordIndices.push_back(index);
    }

    TrieNode* getRoot() {
        return root;
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;

            TrieNode* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                if (node->children.find(target[j]) == node->children.end()) {
                    break;
                }
                node = node->children[target[j]];
                for (int idx : node->wordIndices) {
                    int len = words[idx].size();
                    int cost = costs[idx];
                    if (j + 1 <= n && j + 1 - i == len) {
                        dp[j + 1] = min(dp[j + 1], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
