class Node {
public:
    unordered_map<char, Node*> child;
    int cost;

    Node() : cost(INT_MAX) {}
};

class Trie {
public:
    Node* root;

    Trie() {
        root = new Node();
    }

    void insert(const string& word, int cost) {
        Node* node = root;
        for (char c : word) {
            if (!node->child.count(c)) {
                node->child[c] = new Node();
            }
            node = node->child[c];
        }
        node->cost = min(node->cost, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            Node* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->child.count(target[j])) break;
                node = node->child[target[j]];
                if (node->cost != INT_MAX) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + node->cost);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
