int n, m;
string tt;
vector<int> dp;

// Trie with classes
class Node{
    public:
    Node *child[26];
    int prefix;
    vector<string> wend;
    int mn;
    Node(){
        mn = 1e9;
        prefix = 0;
        for(int i = 0; i < 26; ++i) child[i] = NULL;
    }
};

class Trie {
public:
    Node *root;
    Trie() {
        root = new Node;
    }
    void insert(string s, int cost) {
        Node *cur = root;
        for(int i = 0; i < s.size(); ++i){
            cur->prefix++;
            int x = s[i] - 'a';
            if(cur -> child[x] == NULL) cur -> child[x] = new Node;
            cur = cur -> child[x];
        }
        cur->prefix++;
        (cur -> wend).push_back(s);
        cur->mn = min(cur->mn, cost);
    }
    int f(int ix){
        Node *cur = root;
        int best = 1e9;
        for(int i = ix; i < n; ++i){
            int x = tt[i] - 'a';
            if(cur->child[x] == NULL) return best;
            else{
                cur = cur->child[x];
                if(cur->mn < 1e9) best = min(best, cur->mn + dp[i + 1]);
            }
        }
        return best;
    }
    int search(string s, int f = 0) { // f = 0 when we have to search for prefix
        Node *cur = root;
        for(int i = 0; i < s.size(); ++i){
            int x = s[i] - 'a';
            if(cur->child[x] == NULL) return 0;
            else cur = cur->child[x];
        }
        if(f == 0) return 1;
        return (!(cur->wend).empty());
    }
};


class Solution {
public:
    int minimumCost(string t, vector<string>& v, vector<int>& c) {
        n = t.size(), m = c.size();
        tt = t;
        dp = vector<int>(n + 1, 1e9);

        Trie tree;
        for(int i = 0; i < m; ++i) tree.insert(v[i], c[i]);

        dp[n] = 0;
        for(int i = n - 1; i >= 0; --i){
            dp[i] = tree.f(i);
        }
        if(dp[0] >= 1e9) return -1;
        return dp[0];
    }
};
