class Solution {
public:

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = 0;
        for(auto& j: words)n+=j.length();
        n++;
        vector<vector<int>> trie(27, vector(n, 0));
        int t = 0;
        int w = 0;
        for(auto& j: words){
            int idx = 0;
            for(int i=0; i<j.length(); i++){
                int v = j[i]-'a';
                if(trie[v][idx]){
                    idx=trie[v][idx];
                }
                else{
                    trie[v][idx]=++t;
                    idx=t;
                }
            }
            if(trie[26][idx]){
                trie[26][idx]=min(costs[w], trie[26][idx]);
            }
            else{
                trie[26][idx]=costs[w];
            }
            w++;
        }
        n=target.length();
        int bad = 1e9;
        vector<int> dp(n+1, bad);
        dp[0]=0;
        for(int j=0; j<n; j++){
            if(dp[j]==bad)continue;
            int idx = 0;
            for(int i=j; i<n; i++){
                int v = target[i]-'a';
                if(trie[v][idx]){
                    idx=trie[v][idx];
                    if(trie[26][idx])dp[i+1]=min(dp[i+1], dp[j]+trie[26][idx]);
                }
                else{
                    break;
                }
            }
        }
        return((dp[n]==bad?-1:dp[n]));
    }
};
