struct TrieNode {
    unordered_map<char, TrieNode*> children;
    int index = -1;
};

class Trie {
public:
    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->index = index;
    }

    TrieNode* getRoot() {
        return root;
    }

private:
    TrieNode* root;
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.length();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        map<string,int>mp;
        for(int i=0;i<words.size();i++){
            if(mp.count(words[i])) mp[words[i]]=min(costs[i],mp[words[i]]);
            else mp[words[i]]=costs[i];
        }
        for(int i=0;i<words.size();i++) costs[i]=mp[words[i]];

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i);
        }

        for (int i = 0; i <= n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.getRoot();
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                if (node->index != -1) {
                    dp[j + 1] = min(dp[j + 1], dp[i] + costs[node->index]);
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
