class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')
        self.is_end = False

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word: str, cost: int):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.is_end = True
        node.cost = min(cost,node.cost)

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        trie = Trie()

        # Insert all words into the trie with their respective costs
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        # Dynamic programming array
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  # No cost to form an empty string

        # Fill the dp array
        for i in range(n):
            if dp[i] == float('inf'):
                continue
            node = trie.root
            for j in range(i, n):
                if target[j] in node.children:
                    node = node.children[target[j]]
                    if node.is_end:
                        dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)
                else:
                    break

        return dp[n] if dp[n] != float('inf') else -1
