class TrieNode {
public:
    unordered_map<char, TrieNode*> child;
    vector<pair<int, int>> wordends;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int ind, int cost) {
        TrieNode* node = root;
        for (int i=0;i<word.size();i++) {
            char x = word[i];
            if (!node->child.count(x)) {
                node->child[x] = new TrieNode();
            }
            node = node->child[x];
        }
        node->wordends.push_back({ind, cost});
    }
};

class Solution {
public:
    int  minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->child.count(target[j])) {
                    break;
                }
                node = node->child[target[j]];
                for (auto& p : node->wordends) {
                    int word_sz = words[p.first].size();
                    if (i + word_sz <= n) {
                        dp[i + word_sz] = min(dp[i + word_sz], dp[i] + p.second);
                    }
                }
            }
        }

        return dp[n] == INT_MAX? -1 : dp[n];
    }
};
