class TrieNode:
    def __init__(self):
        self.child = {}
        self.is_end_of_word = False
        self.c = float('inf')

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, w, c):
        node = self.root
        for char in w:
            if char not in node.child:
                node.child[char] = TrieNode()
            node = node.child[char]
        node.is_end_of_word = True
        node.c = min(node.c, c)

    def search(self, target, start):
        node = self.root
        matches = []
        for i in range(start, len(target)):
            if target[i] not in node.child:
                break
            node = node.child[target[i]]
            if node.is_end_of_word:
                matches.append((i + 1, node.c))
        return matches

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0

        trie = Trie()
        for w, c in zip(words, costs):
            trie.insert(w, c)

        for i in range(n):
            if dp[i] == float('inf'):
                continue
            for end, c in trie.search(target, i):
                dp[end] = min(dp[end], dp[i] + c)

        return dp[n] if dp[n] != float('inf') else -1
