class TrieNode {
    val c = mutableMapOf<Char, TrieNode>()
    var t = Int.MAX_VALUE
}

class Trie {
    val r = TrieNode()

    fun i(w: String, t: Int) {
        var n = r
        for (ch in w) {
            if (!n.c.containsKey(ch)) {
                n.c[ch] = TrieNode()
            }
            n = n.c[ch]!!
        }
        n.t = kotlin.math.min(n.t, t)
    }
}

class Solution {
    fun minimumCost(t: String, w: Array<String>, c: IntArray): Int {
        val n = t.length
        val tr = Trie()
        for (i in w.indices) {
            tr.i(w[i], c[i])
        }

        val d = IntArray(n + 1) { Int.MAX_VALUE }
        d[0] = 0

        for (i in 0 until n) {
            if (d[i] == Int.MAX_VALUE) continue
            var node = tr.r
            for (j in i until n) {
                if (!node.c.containsKey(t[j])) break
                node = node.c[t[j]]!!
                if (node.t != Int.MAX_VALUE) {
                    d[j + 1] = kotlin.math.min(d[j + 1], d[i] + node.t)
                }
            }
        }

        return if (d[n] == Int.MAX_VALUE) -1 
        else d[n]
    }
}
