#define ll long long


class TrieNode{
    public:
        map<char, TrieNode*> m;
        bool is_end; ll ec{INT_MAX};
        TrieNode(bool tr){this->is_end = tr;};
        TrieNode(){is_end = false;};
        TrieNode* insert(char c){
            auto it = m.find(c);
            if(it == m.end()){
                m[c] = new TrieNode();
                return m[c];
            }return m[c];
        }
};
class Trie{
    public:
        TrieNode* root;
        Trie(){root = new TrieNode();}
        bool insert(string s, ll c){
            auto curr = root;
            for(char i:s){
                auto nxt = curr->insert(i);
                curr = nxt;
            }
            curr->is_end = true; curr->ec=min(curr->ec, c);
            return true;
        }
        bool search(string s){
            auto curr = root;
            for(auto i:s){
                auto it = curr->m.find(i);
                if(it == curr->m.end()){return false;}
                curr = it->second;
            }if(curr->is_end) return true;
            return false;
        }
};
class Solution {
public:
    string target;Trie t;
    vector<ll> dp;
    ll get_cost(int i){
        auto curr = t.root; int ts = target.size();
        if(i==ts) return 0; int si = i;
        if(dp[i]!=-1) return dp[i];
        ll ans{INT_MAX};
        while(i<ts){
            if(curr->is_end){
                ans = min(ans, curr->ec+get_cost(i));
            }
            auto next = curr->m[target[i]];
            if(!next) break;
            i++;
            curr=next;
        }
        if(i==ts && curr->is_end) ans=min(ans, curr->ec);
        dp[si]=ans;
        return ans;
    }
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = words.size();
        for(int i = 0;i<n;i++) t.insert(words[i], costs[i]);
        this->target=target; 

        int ts = target.size();
        dp.resize(ts, -1);

        ll ans = get_cost(0);
        if(ans>=INT_MAX) return -1;
        return ans;

    }
};
