class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = None

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word, cost):
        node = self.root
        for char in word:
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = cost

    def search(self, target, start):
        node = self.root
        results = []
        for i in range(start, len(target)):
            if target[i] not in node.children:
                break
            node = node.children[target[i]]
            if node.cost is not None:
                results.append((i + 1, node.cost))
        return results

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        word_cost_dict = {}

        for word, cost in zip(words, costs):
            if word not in word_cost_dict or cost < word_cost_dict[word]:
                word_cost_dict[word] = cost

        unique_words = list(word_cost_dict.keys())
        min_costs = list(word_cost_dict.values())
        costs = min_costs
        words = unique_words
        import sys
        inf = sys.maxsize

        n = len(target)
        dp = [inf] * (n + 1)
        dp[0] = 0

        trie = Trie()
        for word, cost in zip(words, costs):
            trie.insert(word, cost)

        for i in range(n):
            if dp[i] == inf:
                continue
            matches = trie.search(target, i)
            for end, cost in matches:
                dp[end] = min(dp[end], dp[i] + cost)

        return dp[n] if dp[n] != inf else -1
