class Trie:
    def __init__(self, cost=inf):
        self.cost = cost
        self.cs = [None] * 26
    def get(self, c):
        c = ord(c) - 97
        if self.cs[c] is None:
            self.cs[c] = Trie()
        return self.cs[c]
class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        d = [0] + [inf] * len(target)
        t = Trie()
        for w, x in zip(words, costs):
            u = t
            for c in w:
                u = u.get(c)
            u.cost = min(u.cost, x)
        for i in range(len(target)):
            if d[i] < inf:
                u = t
                for j in range(i, len(target)):
                    if u.cs[ord(target[j]) - 97] is None:
                        break
                    u = u.get(target[j])
                    d[j+1] = min(d[j+1], d[i] + u.cost)
        return d[len(target)] if d[len(target)] < inf else -1
