class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> word_ends; // (index, cost)

    TrieNode() {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int index, int cost) {
        TrieNode* node = root;
        for (char ch : word) {
            if (!node->children.count(ch)) {
                node->children[ch] = new TrieNode();
            }
            node = node->children[ch];
        }
        node->word_ends.push_back({index, cost});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, numeric_limits<int>::max());
        dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == numeric_limits<int>::max()) {
                continue;
            }

            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                char ch = target[j];
                if (node->children.find(ch) == node->children.end()) {
                    break;
                }
                node = node->children[ch];
                for (auto& [word_index, word_cost] : node->word_ends) {
                    int word_len = words[word_index].size();
                    if (i + word_len <= n) {
                        dp[i + word_len] = min(dp[i + word_len], dp[i] + word_cost);
                    }
                }
            }
        }

        return dp[n] == numeric_limits<int>::max() ? -1 : dp[n];
    }
};
