class Solution {
public:

     class Node {
         public:
        map<char, Node*> mp;
        vector<pair<int, int>> endswith; 

         bool ispresent(char ch)
         {
             return mp.count(ch);
         }
         Node* get(char ch)
         {
             return mp[ch];
         }
         void put(char ch,Node* node)
         {
             mp[ch]=node;
         }   
    };

    class Trie {
    public:
        Node* root;
        Trie() { 
            root = new Node(); 
        }

        void insert(string& word, int i, int c) {
            Node* node = root;
            for (auto it:word) {
                if (!node->ispresent(it)){
                    node->put(it,new Node());
                }
                  node=node->get(it);  
            }
            node->endswith.push_back({i, c});
        }
    };

    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> dp(n + 1, 1e9);
        dp[0] = 0;

        Trie* trie=new Trie();
        for (int i = 0; i < words.size(); ++i) {
            trie->insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
           if(dp[i] != 1e9){
            Node* node = trie->root;
            for (int prev = i; prev < n; prev++) {
                if(node->ispresent(target[prev]))
                {
                    node=node->get(target[prev]);
                    for(auto it:node->endswith)
                    {
                        int ind=it.first;
                        int cost=it.second;
                        int sz=words[ind].size();
                        if(i+sz>n)continue;
                        dp[i+sz]=min(dp[i+sz],dp[i]+cost);
                    }
                }
                else
                {
                    break;
                }             
            }
           }
        }
        if(dp[n]>=1e9)
        {
            dp[n]=-1;
        }
        return dp[n];

    }
};
