class TrieNode:
    def __init__(self):
        self.children = {}
        self.cost = float('inf')  # Initialize cost as infinity

def build_trie(words, costs):
    root = TrieNode()
    for word, cost in zip(words, costs):
        node = root
        for char in reversed(word):  # Build trie in reverse order to facilitate reverse matching
            if char not in node.children:
                node.children[char] = TrieNode()
            node = node.children[char]
        node.cost = min(node.cost, cost)  # In case of duplicate words, store the minimum cost
    return root

class Solution:
    def minimumCost(self, target: str, words: List[str], costs: List[int]) -> int:
        n = len(target)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0  # Base case

        trie_root = build_trie(words, costs)

        # Update dp array using the trie to match substrings in target
        for j in range(n):
            # Start matching from this position backwards
            node = trie_root
            for i in range(j, -1, -1):
                char = target[i]
                if char in node.children:
                    node = node.children[char]
                    if node.cost < float('inf'):
                        dp[j + 1] = min(dp[j + 1], dp[i] + node.cost)
                else:
                    break  # No more matches possible, break the inner loop

        return dp[n] if dp[n] != float('inf') else -1
