class Trie {
public:
    int cost;
    Trie* children[26];

    Trie() : cost(0), children{0} {

    }

    ~Trie() {
        for (auto i : children) {
            delete i;
        }
    }

    void insert(string word, int cost) {
        auto it = this;
        for (int i = 0; i < word.size(); ++i) {
            if (!it->children[word[i] - 'a']) {
                it->children[word[i] - 'a'] = new Trie();
            }
            it = it->children[word[i] - 'a'];
            if (i + 1 == word.size()) {
                it->cost = it->cost ? min(it->cost, cost) : cost;
            }
        }
    }
};

class Solution {
public:
		int minimumCost(string s, vector<string>& words, vector<int>& costs) {
			Trie* trie = new Trie();
			for (int i = 0; i < words.size(); ++i) {
				trie->insert(words[i], costs[i]);
			}

			vector<unsigned> minCost(s.size() + 1, -1);
			minCost[0] = 0;

			for (int i = 1; i <= s.size(); ++i) {
				if (minCost[i - 1] != -1 && trie->children[s[i - 1] - 'a']) {
					auto it = trie;
					for (int j = i; j <= s.size(); ++j) {
						it = it->children[s[j - 1] - 'a'];
						if (!it) {
							break;
						}

						if (it->cost) {
							minCost[j] = min(minCost[j], minCost[i - 1] + it->cost);
						}
					}
				}
			}

			return minCost[s.size()];
		}
};
