class TrieNode {
public:
    unordered_map<char, TrieNode*> child;
    vector<pair<int, int>> words_end;
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int index, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->child.count(c)) {
                node->child[c] = new TrieNode();
            }
            node = node->child[c];
        }
        node->words_end.push_back({index, cost});
    }
};

class Solution {
public:
    int  minimumCost(string target, vector<string>& words, vector<int>& costs) {
        const int n = target.size();
        vector<int> dp(n + 1, INT_MAX);

        dp[0] = 0;

        Trie te;
        for (int i = 0; i < words.size(); ++i) {
            te.insert(words[i], i, costs[i]);
        }

        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) {
                continue;
            }

            TrieNode* node = te.root;
            for (int j = i; j < n; ++j) {

                if (!node->child.count(target[j])) {
                    break;
                }

                node = node->child[target[j]];
                for (auto& [idx, cost] : node->words_end) {
                    int w_len = words[idx].size();

                    if (i + w_len <= n) {
                        dp[i + w_len] = min(dp[i + w_len], dp[i] + cost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX? -1 : dp[n];
    }
};
