class Solution {
    class Node {
        Node[] children;
        int cost;
        public Node() {
            children = new Node[26];
            cost = -1;
        }
    }
    public void insert(Node u, char[] word, int idx, int cost) {
        if(idx == word.length) {
            if(u.cost == -1 || cost < u.cost) {
                u.cost = cost;
            }
            return;
        }
        if(u.children[word[idx]-'a'] == null) {
            u.children[word[idx]-'a'] = new Node();
        }
        insert(u.children[word[idx]-'a'], word, idx+1, cost);
    }
    public int minimumCost(String target, String[] words, int[] costs) {
        Node root = new Node();
        for(int i = 0; i < words.length; i++) {
            insert(root, words[i].toCharArray(), 0, costs[i]);
        }
        char[] tar = target.toCharArray();
        int[] dp = new int[tar.length+1];
        for(int i = tar.length-1; i >= 0; i--) {
            dp[i] = -1;
            int idx = i;
            Node u = root.children[tar[idx]-'a'];
            while(u != null) {
                if(u.cost != -1) {
                    int temp = dp[idx+1];
                    if(temp != -1 && (dp[i] == -1 || temp+u.cost < dp[i])) {
                        dp[i] = temp+u.cost;
                    }
                }
                if(idx == tar.length-1) {
                    break;
                }
                idx++;
                u = u.children[tar[idx]-'a'];
            }
        }
        return dp[0];
    }
}
