class MohitTrie {
public:
    unordered_map<char, MohitTrie*> mohitchild;
    vector<pair<int, int>> mohitendwor;
};

class Trie {
public:
    MohitTrie* mohitroot;

    Trie() {
        mohitroot = new MohitTrie();
    }

                   void insert(string word, int index, int cost) {
        MohitTrie* node = mohitroot;
        // ifheiufhq
                       for (char c : word) {
            if (!node->mohitchild.count(c)) {
                                   node->mohitchild[c] = new MohitTrie();
            }
                      node = node->mohitchild[c];
        }
        node->mohitendwor.push_back({index, cost});
    }
};
