class Solution {
public:
    int  minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        vector<int> mohitag_dp(n + 1, INT_MAX);
        mohitag_dp[0] = 0;

        Trie trie;
        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], i, costs[i]);
        }
// nfrkfn
//         fbruifeuier
//         jfbejf
        for (int i = 0; i < n; ++i) {
            if (mohitag_dp[i] == INT_MAX) {
                continue;
            }

            MohitTrie* node = trie.mohitroot;
            for (int j = i; j < n; ++j) {
                if (!node->mohitchild.count(target[j])) {
                    break;
                }
//                 fehuifhei
//                 fhg4uy3fgowdqhcbnsj

                node = node->mohitchild[target[j]];
                for (auto& p : node->mohitendwor) {
                    int word_len = words[p.first].size();
                    if (i + word_len <= n) {
                        mohitag_dp[i + word_len] = min(mohitag_dp[i + word_len], mohitag_dp[i] + p.second);
                    }
                }
            }
        }

        return mohitag_dp[n] == INT_MAX? -1 : mohitag_dp[n];
    }
};
