class TrieNode {
public:
    TrieNode* key[26];
    bool isEndOfWord = false;
    int cost = 1e9;
};

class Trie {
public:
    TrieNode* root;
    Trie() {
        root = new TrieNode();
    }

    void insert(string word, int cost) {
        TrieNode* node = root;

        for(auto c: word){
            if(node->key[(c - 'a')]){
                node = node->key[(c - 'a')];
            }else{
                node->key[(c - 'a')] = new TrieNode();
                node = node->key[(c - 'a')];
            }
        }

        node->isEndOfWord = true;
        node->cost = cost;
    }
};


class Solution {
public:


    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = words.size();
        TrieNode* root = new TrieNode();

        for(int i=0; i<n; i++){
            string word = words[i];

            TrieNode* node = root;
            for(auto c: word){
                if(node->key[(c - 'a')]){
                    node = node->key[(c - 'a')];
                }else{
                    node->key[(c - 'a')] = new TrieNode();
                    node = node->key[(c - 'a')];
                }
            }

            node->isEndOfWord = true;
            node->cost = min(node->cost, costs[i]);
        }

        vector<int> dp(target.size() + 1, 0);
        int x = target.size();
        for(int index=x-1; index>=0; index--){
            int minCost = 1e9;

            TrieNode* node = root;
            for(int i=index; i<x; i++){
                if(node->key[(target[i] - 'a')]){
                    node = node->key[(target[i] - 'a')];
                }else{
                    break;
                }

                if(node->isEndOfWord){
                    minCost = min(minCost, node->cost + dp[i + 1]);
                }
            }

            dp[index] = minCost;
        }

        int result = dp[0];

        if(result >= 1e9){
            return -1;
        }

        return result;
    }
};
