import java.util.*;

class Solution {
    // Define the TrieNode class
    class TrieNode {
        Map<Character, TrieNode> children = new HashMap<>();
        int minCost = Integer.MAX_VALUE;
    }

    // Define the Trie class
    class Trie {
        TrieNode root = new TrieNode();

        // Method to insert words with their costs into the Trie
        public void insert(String word, int cost) {
            TrieNode node = root;
            for (char c : word.toCharArray()) {
                node.children.putIfAbsent(c, new TrieNode());
                node = node.children.get(c);
            }
            node.minCost = Math.min(node.minCost, cost);
        }

        // Method to match words in the Trie with the target string and update the dp array
        public void match(String target, int start, int[] dp) {
            TrieNode node = root;
            for (int i = start; i < target.length(); i++) {
                char c = target.charAt(i);
                node = node.children.get(c);
                if (node == null) break;
                if (node.minCost != Integer.MAX_VALUE && dp[start] != Integer.MAX_VALUE) {
                    dp[i + 1] = Math.min(dp[i + 1], dp[start] + node.minCost);
                }
            }
        }
    }

    // Method to calculate the minimum cost to form the target string
    public int minimumCost(String target, String[] words, int[] costs) {
        int n = target.length();
        int[] dp = new int[n + 1];
        Arrays.fill(dp, Integer.MAX_VALUE);
        dp[0] = 0;

        Trie trie = new Trie();
        for (int i = 0; i < words.length; i++) {
            trie.insert(words[i], costs[i]);
        }

        for (int i = 0; i < n; i++) {
            if (dp[i] == Integer.MAX_VALUE) continue;
            trie.match(target, i, dp);
        }

        return dp[n] == Integer.MAX_VALUE ? -1 : dp[n];
    }
}
