#include <vector>
#include <unordered_map>
#include <string>
#include <climits>
using namespace std;

class TrieNode {
public:
    unordered_map<char, TrieNode*> children;
    vector<pair<int, int>> e;

    TrieNode() {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void add_word(string& word, int idx, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c))
                node->children[c] = new TrieNode();
            node = node->children[c];
        }
        node->e.push_back({idx, cost});
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        int n = target.size();
        Trie trie;
        vector<int> dp(n + 1, INT_MAX);
        dp[0] = 0;
        for (int i = 0; i < words.size(); ++i) {
            trie.add_word(words[i], i, costs[i]);
        }
        for (int i = 0; i < n; ++i) {
            if (dp[i] == INT_MAX) continue;
            TrieNode* node = trie.root;
            for (int j = i; j < n; ++j) {
                if (!node->children.count(target[j])) break;
                node = node->children[target[j]];
                for (auto& p : node->e) {
                    int wordIndex = p.first;
                    int wordCost = p.second;
                    int len = words[wordIndex].size();
                    if (i + len <= n) {
                        dp[i + len] = min(dp[i + len], dp[i] + wordCost);
                    }
                }
            }
        }

        return dp[n] == INT_MAX ? -1 : dp[n];
    }
};
