struct TrieNode {
    unordered_map<char, TrieNode*> children;
    int minCost;

    TrieNode() : minCost(1e9) {}
};

class Trie {
public:
    TrieNode* root;

    Trie() {
        root = new TrieNode();
    }

    void insert(const string& word, int cost) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->minCost = min(node->minCost, cost);
    }
};

class Solution {
public:
    int minimumCost(string target, vector<string>& words, vector<int>& costs) {
        Trie trie;
        int n = target.size();

        for (int i = 0; i < words.size(); ++i) {
            trie.insert(words[i], costs[i]);
        }

        vector<int> dp(n + 1, 1e9);
        dp[0] = 0; 

        for (int j = 0; j < n; ++j) {
            if (dp[j] == 1e9) continue;

            TrieNode* node = trie.root;
            for (int k = j; k < n; ++k) {
                if (!node->children.count(target[k])) break;
                node = node->children[target[k]];
                if (node->minCost != 1e9) {
                    dp[k + 1] = min(dp[k + 1], dp[j] + node->minCost);
                }
            }
        }

        return dp[n] == 1e9 ? -1 : dp[n];
    }
};
