class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<int,int>> pq;
        long long r=1,c=1;
        for(int i=0;i<m-1;i++)
            pq.push({horizontalCut[i],1});
        for(int i=0;i<n-1;i++)
            pq.push({verticalCut[i],2});
        long long ans=0;
        while(!pq.empty())
        {
            int r_c=pq.top().second;
            int t=pq.top().first;
            pq.pop();
            if(r_c==1)
            {
                ans+=c*t;
                r++;
            }
            else
            {
                ans+=r*t;
                c++;
            }
        }
        return ans;
    }
};
