class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
         int mr=1,mc=1;
         priority_queue<int>q(hor.begin(),hor.end());
         priority_queue<int>q2(ver.begin(),ver.end());
        long long ans=0;
        while(!(q.empty())||!q2.empty()){
            int t1=0,t2=0;
            if(!(q.empty())){t1=q.top();
                           }
            if(!(q2.empty())){t2=q2.top();
                             }

            if(t1>t2){
                ans+=t1*mr;
                mc++;
                 q.pop();
            }
            else{
                 ans+=t2*mc;
                mr++;
                 q2.pop();
            }
        }
        return ans;
    }
};
