class Solution {
    public void reverse(int[] arr){
        int i=0,j=arr.length-1;
        while(i<j){
            int temp=arr[i];
            arr[i]=arr[j];
            arr[j]=temp;
            i++;
            j--;

        }
        return;
    }
    public long minimumCost(int m, int n, int[] hc, int[] vc) {
        Arrays.sort(hc);
        Arrays.sort(vc);
        reverse(hc);
        reverse(vc);
        long hp=1,vp=1;
        int i=0,j=0;
        long cost=0;
        while(i<hc.length && j<vc.length){
            if(hc[i]>vc[j]){
                cost+=(long)(vp*hc[i]);
                hp++;
                i++;
            }
            else{
                cost+=(long)(vc[j]*hp);
                vp++;
                j++;
            }
        }
        while(i<hc.length){
            cost+=(long)(vp*hc[i]);
            hp++;
            i++;
        }
        while(j<vc.length){
             cost+=(long)(vc[j]*hp);
            vp++;
            j++;
        }
        return cost;
    }
}
