class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        r, c = 1, 1

        horizontalCut.sort()
        verticalCut.sort()

        ans = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] >= verticalCut[-1]:
                x = horizontalCut.pop()
                ans += x * c
                r += 1
            else:
                x = verticalCut.pop()
                ans += x * r
                c += 1

        while horizontalCut:
            x = horizontalCut.pop()
            ans += x * c

        while verticalCut:
            x = verticalCut.pop()
            ans += x * r

        return ans
