class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        hcnt = vcnt = 1

        arr = [(h, True) for h in horizontalCut] + [(v, False) for v in verticalCut]
        arr.sort(reverse=True)

        for val, flag in arr:

            if flag:
                ans += val * hcnt
                vcnt += 1
            else:
                ans += val * vcnt
                hcnt += 1

        return ans
