class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
           vector<pair<int,int>>v1;
          vector<pair<int,int>>v2;
            for(int i=0;i<m-1;i++)
            {
                 v1.push_back({hor[i],i});
            }
         for(int i=0;i<n-1;i++)
            {
                 v2.push_back({ver[i],i});
            }
         sort(v1.begin(),v1.end());
         sort(v2.begin(),v2.end());
        long long ans=0;
         int horc=1,verc=1;
          int i=m-2,j=n-2;
         while(i>=0 && j>=0)
         {
              if(v1[i].first>v2[j].first)
              {
                   ans+=((long long )(v1[i].first)*(long long)verc);
                  i--;
                  horc++;
              }
             else {
                  ans+=((long long)(v2[j].first)*((long long)horc));
                  j--;
                  verc++;
             }

         }
        while(i>=0)
        {
             ans+=((long long )(v1[i].first)*(long long )(verc));
                  i--;
                  horc++;
        }
         while(j>=0)
        {
             ans+=((long long )(v2[j].first)*(long long )(horc));
                  j--;
                  verc++;
        }
        return ans;
    }
};
