class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<vector<int>> aux;

        for (int& i: horizontalCut) {
            aux.push_back({i, 0});
        }

        for (int& i: verticalCut) {
            aux.push_back({i, 1});
        }

        sort(aux.begin(), aux.end(), greater<vector<int>>());
        vector<int> cnt = {1, 1};
        long long res = 0;

        for (auto& a: aux) {
            res += 1LL * cnt[!a[1]] * a[0];
            cnt[a[1]]++;
        }

        return res;
    }
};
