class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        priority_queue<pair<long long, char>>pq;
        for(long long x:horizontalCut) pq.push({x, 'H'});
        for(long long x:verticalCut) pq.push({x, 'V'});

        long long cost = 0;
        long long h = 1, v = 1;
        while(!pq.empty()){
            pair<long long, char> temp = pq.top();
            pq.pop();
            if(temp.second == 'H'){
                cost += temp.first * v;
                h++;
            }
            else{
                cost += temp.first * h;
                v++;
            }
        }
        return cost;
    }
};
