public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
                    var list = new List<(int val, int axis)>(m + n - 2);
            foreach (var num in horizontalCut)
            {
                list.Add((num, 0));
            }

            foreach (var num in verticalCut)
            {
                list.Add((num, 1));
            }

            list.Sort((a, b) => b.val.CompareTo(a.val));
            long ret = 0,h = 1, v = 1;
            foreach (var (val, axis) in list)
            {
                if (axis == 0)
                {
                    ret += val * v;
                    h++;
                }
                else
                {
                    ret += val * h;
                    v++;
                }
            }

            return ret;
    }
}
