class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horCut, vector<int>& vertCut) {
        long long ver=1,hor=1;
        sort(horCut.begin(),horCut.end(),greater<int>());
        sort(vertCut.begin(),vertCut.end(),greater<int>());
        int i=0,j=0;
        long long ans=0;
        while(i<m-1 && j<n-1)
        {
            if(horCut[i]>vertCut[j])
            {
                ans+=ver*(long long)horCut[i];
                hor++;
                i++;
            }
            else
            { 
                ans+=hor*(long long)vertCut[j];
                ver++;
                j++;
            }
        }
        long long tot=0;
        while(i<m-1)
        {

            tot+=(long long)horCut[i]*ver;
            i++;
        }
        while(j<n-1)
        {
            tot+=(long long)vertCut[j]*hor;
            j++;
        }
        ans+=tot;
        return ans;
    }
};
