import java.util.*;

class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // Sort the cuts in descending order
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        int h = horizontalCut.length - 1;
        int v = verticalCut.length - 1;

        int horizontalPieces = 1;
        int verticalPieces = 1;
        long totalCost = 0;

        // Process the cuts in a greedy manner
        while (h >= 0 && v >= 0) {
            if (horizontalCut[h] > verticalCut[v]) {
                totalCost += horizontalCut[h] * verticalPieces;
                horizontalPieces++;
                h--;
            } else {
                totalCost += verticalCut[v] * horizontalPieces;
                verticalPieces++;
                v--;
            }
        }

        // Process any remaining horizontal cuts
        while (h >= 0) {
            totalCost += horizontalCut[h] * verticalPieces;
            horizontalPieces++;
            h--;
        }

        // Process any remaining vertical cuts
        while (v >= 0) {
            totalCost += verticalCut[v] * horizontalPieces;
            verticalPieces++;
            v--;
        }

        return totalCost;
    }
}
