class Solution {
public:
	long long minimumCost(int n, int m, vector<int>& a, vector<int>& b) {
		using ll = long long;

		priority_queue<int> qa(a.begin(), a.end());
		priority_queue<int> qb(b.begin(), b.end());
		ll ca = 1, cb = 1;
		ll res = 0;
		while (!qa.empty() || !qb.empty())
		{
			if (qa.empty() || (!qb.empty() && qa.top() < qb.top()))
			{
				res += cb * qb.top();
				qb.pop();
				ca += 1;
			}
			else
			{
				res += ca * qa.top();
				qa.pop();
				cb += 1;
			}
		}

		return res;
	}
};
