class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        long long horizontalCuts = 1;
        long long verticalCuts = 1;

        int i = 0, j = 0;
        long long totalCost = 0;

        while (i < h.size() && j < v.size()) {
            if (h[i] >= v[j]) {
                totalCost += static_cast<long long>(h[i]) * verticalCuts;
                horizontalCuts++;
                i++;
            } else {
                totalCost += static_cast<long long>(v[j]) * horizontalCuts;
                verticalCuts++;
                j++;
            }
        }

        while (i < h.size()) {
            totalCost += static_cast<long long>(h[i]) * verticalCuts;
            i++;
        }

        while (j < v.size()) {
            totalCost += static_cast<long long>(v[j]) * horizontalCuts;
            j++;
        }

        return totalCost;
    }
};
