class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,bool>> v;
        for(auto n : horizontalCut) {
            v.push_back({n,false});
        }
        for(auto n : verticalCut) {
            v.push_back({n,true});
        }
        sort(v.rbegin(), v.rend());
        long long ans = 0, vc = 1, hc = 1;
        for(int i=0; i<v.size(); i++) {
            if(v[i].second) {
                ans += v[i].first * (hc);
                vc++;
            } else {
                ans += v[i].first * (vc);
                hc++;
            }
        }
        return ans;
    }
};
