class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        long min = 0;
        int hor = horizontalCut.length-1,ver = verticalCut.length-1;
        int h = 1,v = 1;
        while(hor >= 0 && ver >= 0){
            if(horizontalCut[hor] > verticalCut[ver]){
                min += horizontalCut[hor] * v;
                h++;
                hor--;
            }else{
                min += verticalCut[ver] * h;
                v++;
                ver--;
            }
        }

        while(hor >= 0){
            min += horizontalCut[hor] * v;
            hor--;
        }
        while(ver >= 0){
            min += verticalCut[ver] * h;
            ver--;
        }

        return min;
    }
}
