class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
         vector<pair<int, bool>> x;

    for (int i = 0; i <hc.size(); i++) {
        x.push_back({hc[i], 1});
    }


    for (int j = 0; j < vc.size(); j++) {
        x.push_back({vc[j], 0});
    }

    sort(x.rbegin(), x.rend());

    long long count_h = 1;
    long long count_v = 1;
    long long ans = 0;

    for (auto it : x) {
        int cost = it.first;
        bool f = it.second;

        if (f) {

            ans += cost * count_v;
            count_h++;
        } else {

            ans += cost * count_h;
            count_v++;
        }
    }

    return ans;
    }
};
